import { css, LitElement, html } from './lit-element/lit-element.js';
import { Link, Routal } from '/routal.js'

export class aButton extends LitElement {

    static get properties() {
      return {
        contentId:{type:String},
        buttonType:{type:String},
        buttonContent:{type:Object},
        buttonText:{type:String},
        clickCount:{type:String},
        clicked:{type:Boolean},
        contentType:{type:String},
        laughorlove:{type:String}
      }
    }

    static get styles(){
      return css`

        button svg{
          height:40px;
          width:40px;
          stroke-width: 8px;
          overflow: visible;
          stroke:#222;
        }

        button[data-laughorlove="undefined"]{
          width: 45%;
          padding: 14px 0;
          border: none;
          margin-right: 34px;
          border-radius: 35px;
          background: transparent;
          border: 3px solid tomato;
        }

        button[data-laughorlove="laugh"] svg{
          stroke-width: unset;
          stroke:unset;
        }

        button svg > path{
          stroke:inherit;
        }

        #container[data-type="pages"],#container[data-type="memes"]{
          width: 35px;
        }



        #container[data-type="pages"] button,#container[data-type="memes"] button{
          border: none;
          background: transparent;
          float: left;
          cursor:pointer;
          outline:none;
        }

        #container[data-type="pages"] span,#container[data-type="memes"] span{
          display: grid;
          place-items: center;
          height: 40px;
        }

        button[data-clicked="true"][data-laughorlove="love"] svg{
          fill:tomato;
          stroke: tomato;
        }

        button[data-clicked="true"][data-laughorlove="laugh"] svg #base{
          fill:#FDDE66;
          stroke: #FDDE66;
        }
        
        button[data-laughorlove="laugh"] svg #base{
          stroke-width: 8px;
          stroke:#444;
        }






        /*
          for text button
        */



        
       #container[data-type="buttons"]{
        width: auto;
        }


        #container[data-type="buttons"] button[data-clicked="true"]{
          background:tomato;
          color:#fff;
          outline:none;
        }

      `
    }

    constructor(){
      super()
      this.clicked = false
      this.clickCount = ''
      this.buttonContent = ''
    }

    async clickButton(){
      this.simulateClick()
      await U.query({ $run: ['like',{contentId:this.contentId, type:this.buttonType}] })
    
    }

    simulateClick(){
      
      this.clicked = this.clicked? false:true
      this.clickCount = this.clicked? this.clickCount + 1:this.clickCount -1
      this.updateData()
    }

    refresh(){
      U.readUser().then(data=>{
        if(data) this.updateData()
      })
    }

    updateData(){
      
      U.query({ $count: {on:'actions', where:{ contentId: this.contentId } } }).then(data=>{
        this.clickCount  = data
      })

      U.query({ $find: {on:'actions', where:{ contentId: this.contentId, sender:'$user.id' } } }).then(data=>{
        if(data[0]) this.clicked = true



      })
      


    }
  
    firstUpdated(){
      this.refresh()
    }

  
    render(){

      let love = html`  

      <svg  width="229" height="195" viewBox="0 0 229 195" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M199.676 10.2907C281.617 66.7097 169.453 194 70.4491 194C-7.36972 104.738 -18.6015 55.5472 29.6266 19.6304C58 -1.5 115.956 29.3193 118.388 76.7169C118.388 30.7032 155.206 -20.3288 199.676 10.2907Z" stroke="black"/>
        </svg>

      
      `

     // let love = html` <img ?data-mode=${this.clicked} src='/images/buttons/love.svg'>`

      if(this.buttonType == 'buttons'){ 
        this.buttonContent = html` ${this.buttonText} `
      }else{
        this.buttonContent = love
        this.laughorlove = 'love'
      }
      
      return html`
     
          <div id="container" data-type="${this.buttonType}">  
            <button  data-laughorlove="${this.laughorlove}" data-clicked="${this.clicked}" @click="${this.clickButton.bind(this)}">${this.buttonContent}</button> <span> ${this.clickCount}</span>  
          </div>
        
      `
    }
  
}

customElements.define('a-button',aButton)
