import { css, LitElement, html } from './lit-element/lit-element.js';
import { Link, Routal } from '/routal.js'


export class renderMeme extends LitElement{

    static get properties(){
        return {
            mouseMoving:Function,
            mouseRemoved:Function,
            mouseClicked:Function,
            memeData:Object,
            upload:Function,
            DPlink:String,
            showTemplate:Boolean,
            templateMemeSelected:Function,
            templateMemeLinks:Array,
            viewType:String
            
        }
    }

    constructor() {
        super()
        this.showTemplate = false
        this.centerElement = this.centerElement.bind(this)
    }


    async firstUpdated(){
      
        this.DPlink = await U.query({$find:{ on:'pages', where:{title: this.memeData.pageTitle} } })
        this.DPlink = this.DPlink[0].DPlink
    }



    static get styles(){
        return css`

            .memeContainer[type="collection"] > *{
              pointer-events: none;
            }

            .memeContainer{
                position: relative;
                font-family:roboto;
                overflow:hidden;
                display:grid;
                place-items:center;
                border-radius: 12px;
                box-shadow: 0 6px 50px #88888859;
                width:94vw;
                color: #222;
                height:94vw;
                text-decoration:none;
                background: linear-gradient(45deg, rgb(199 199 199), rgb(222 222 222));
            }



            

            .memeContainer[type="collection"]{
              width:50vw;
              height:50vw;
              border-radius: 0;
              box-shadow: unset;
            }

            .memeContainer[type="collection"] .overlays div{
              font-size:12px;
              outline:none;
            }

            .memeContainer .mainImage{
                object-fit: cover;
                height:inherit;
                width:inherit;
            }



            .overlays{
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
            }


            .overlays input, .overlays img, .overlays div{
              position:absolute;
              text-align:center;
              transition: transform 0.5s ease;
              font-size:24px;
            }

            .overlays div{
              background:#fff;
              border-radius:10px;
            }

            .overlays *[data-textEdit="false"]{
              cursor:move;
              user-select: none;
              padding:5px;
              outline:none;
            }


            .overlays *[data-textEdit="true"]{
              cursor:text;
              box-shadow: #00000094 6px 6px 15px 500px;
              padding:5px;
              border-radius:15px;
            }



            .overlays input{
              border:none;
              transition: all 0.25s ease;
            }









            .custom-file-input {
                width:61%;
                color: transparent;
              }

              .custom-file-input::-webkit-file-upload-button {
                visibility: hidden;
              }
              .custom-file-input::before {
                content: 'Upload file';
                color: black;
                display: inline-block;

            
                border-radius: 3px;
                padding: 5px;
                outline: none;
                white-space: nowrap;
                -webkit-user-select: none;
                cursor: pointer;
          


                width:82%;
                text-align:center;

                font-size: 18px;
                border-width: 2px;
                border-style: solid;
                border-color: rgb(0, 0, 0);
                background: transparent;
                padding: 6px 12px;
                border-radius: 14px;

              }
              .custom-file-input:hover::before {
                border-color: black;
              }
              .custom-file-input:active {
                outline: 0;
              }
              .custom-file-input:active::before {
                background: -webkit-linear-gradient(top, #e3e3e3, #f9f9f9); 
              }


              .addImage button{
                width:62%;
              }


              button{
                font-size: 18px;
                border-width: 2px;
                border-style: solid;
                border-color: rgb(0, 0, 0);
                background: transparent;
                padding: 6px 12px;
                border-radius: 14px;
              }






              .memeTemplate{
                  position:absolute;
                  z-index:5;
                  top:0;
                  left:0;
                  height:100%;
                  width:100%;
                  background:#222;
                  overflow-y:scroll;
                  color: #fff;
                  display: grid;
                  place-items: center;
                  font-family: roboto;
              }

              .memeTemplate img{

                  width:100%;
                  height:auto;
                  float:left;
                  object-fit:cover;
              }

              @media (min-width: 850px) {
                .memeContainer {
                  width: 500px;
                  height:500px;
                }
  
                
                .memeContainer[type="collection"]{
                  width:300px;
                  height:300px;
                }
  
                .memeContainer[type="collection"] .overlays div{
                  font-size:24px;
                }
  
                .memeContainer .overlays div{
                  font-size:24px;
                }
  
              }
   
        `
    }

    addText(e){

      let charactorCount = e.target.innerText.length
      if(charactorCount == 0 ){
        e.target.classList.add('holder');
      }
      
      if(charactorCount > 0){
        e.target.classList.remove('holder');
      }

        let overlays = this.memeData.overlays
        let divIndex = e.target.dataset.index

        if(e.keyCode == 13){
            overlays[divIndex].textEdit = false
            this.memeData = {...this.memeData}
            return
        }

        if (overlays[divIndex].textEdit== false) return

        

        overlays[divIndex].text = e.target.value
        this.centerElement(overlays[divIndex], e.target, e.target.value)

        //set position, width on initialization 
        this.memeData = {...this.memeData}
    }




    centerElement(overlay,element,newText){
      overlay.width = newText.length * 13

      overlay.left = ((element.parentElement.offsetWidth - overlay.width) / 2)

      overlay.left = (overlay.left/element.parentElement.offsetWidth) * 100

    }

    renderMeme(){
        let overlays = this.memeData.overlays
        let overlayRenderArray = []
  
        for(let index in overlays){
  
          let currentOverlay = overlays[index]

          if(typeof currentOverlay.textEdit == 'undefined'){
            currentOverlay.textEdit = true
            
            
          }

          if(typeof currentOverlay.rotate == 'undefined'){
            currentOverlay.rotate = 0
            this.centerElement(currentOverlay,{parentElement:this.shadowRoot.querySelector('.overlays') },'type here')
          }


          if(typeof currentOverlay.width == 'undefined' &&  currentOverlay.type == 'text') currentOverlay.width = 25
          if(!currentOverlay.text) currentOverlay.text = ''
          
          

          switch(currentOverlay.type){  
            case 'text':
              overlayRenderArray.push(html`
              
              ${currentOverlay.textEdit?html`
              
              <input  
                    
                    tabIndex="-1"
                    ?autofocus=${currentOverlay.textEdit}
                    data-textEdit="${currentOverlay.textEdit}"
                    @keyup="${this.addText.bind(this)}"
                    data-index="${index}" 
                    style="width:${currentOverlay.width}px; top:${currentOverlay.top}%; left:${currentOverlay.left}%; " 
                    placeholder="type here" 
                    value="${currentOverlay.text}"
                    >
              
              
              `:html`
              
              <div  
                    
                    tabIndex="-1"
                    ?autofocus=${currentOverlay.textEdit}
                    data-textEdit="${currentOverlay.textEdit}"
                    @keyup="${this.addText.bind(this)}"
                    @mouseup='${this.mouseRemoved}' 
                    @mousedown='${this.mouseClicked}' 
                    data-index="${index}" 
                    style="width:auto; top:${currentOverlay.top}%; left:${currentOverlay.left}%; transform:scale(${currentOverlay.scale}) rotate(${currentOverlay.rotate}deg);" 
                    placeholder="type here" 
                    >${currentOverlay.text}</div>
              
              
              `}
                    
                
                    
                    `)
              break
            case 'profilePhoto':
              overlayRenderArray.push(html` <img @mouseup='${this.mouseRemoved}' @mousedown='${this.mouseClicked}' data-index="${index}" src="${U.info.serverUrl}/${this.DPlink}"  style="top:${currentOverlay.top}%; left:${currentOverlay.left}%; transform:scale(${currentOverlay.scale});"> `)
              break
          }
        }


       
        return overlayRenderArray
    }

    async renderCanvas(){

      let node = this.shadowRoot.querySelector('.memeContainer')
      domtoimage.toPng(node)
    .then(function (dataUrl) {
        var img = new Image();
        img.src = dataUrl;
        //document.body.appendChild(img);
        U.setMetaTag({property:"og:image"},{content:dataUrl})
        //adds meta tag for facebook and twitter that includes the roast content
    })
    .catch(function (error) {
        console.warn('oops, something went wrong!', error.message);
    });

    
    }



    render(){


        
        return html`

  
        <a class="memeContainer" @click="${Link}" href="/meme/${this.memeData._id}" type="${this.viewType}">
        ${this.memeData.imageLink? html`
        
        <img class='mainImage' @load="${this.renderCanvas.bind(this)}" src='${U.info.serverUrl}/${this.memeData.imageLink}'>
        <div @mousemove='${(e)=>{this.mouseMoving && this.mouseMoving(e,this.shadowRoot)}}'  class="overlays"> ${this.renderMeme.call(this)}</div>
        
        `:html` 
        
        <center class='addImage'>
            <input type="file" class="custom-file-input" @change="${this.upload}">
            <h3>OR</h3>
            <button @click="${this.chooseTemplate}"> Templates</button>
        </center>
        `}




        ${this.showTemplate?html`

            
            
            <div class='memeTemplate'>

              ${this.templateMemeLinks? html`

                  ${this.templateMemeLinks.map(file=>html`
                  <img data-src='${file.filename}' @click='${this.templateMemeSelected}' src='${U.info.serverUrl}/${file.filename}'>
                  `)}

                `:html`loading...`}    
            </div>
            
        
        
        `:html``}
        
        </a>
      `
    }
}


customElements.define('render-meme',renderMeme)
